mapper.update:
UPDATE t_content
title = #{title}, img_url = #{imgUrl}, brief = #{brief}, video_url = #{videoUrl}, type = #{type}, category_id = #{categoryId}, update_by = #{updateBy}, update_time = #{updateTime}, content = #{content}
WHERE id = #{id}

UPDATE t_content
SET view_count = view_count + 1
WHERE id = #{id}

UPDATE t_content
SET comment_count = comment_count+1
WHERE id = #{contentId}
